(205, 'Bài tập cuối tuần Tiếng Anh lớp 5 - Tập 2', 'bai-tap-cuoi-tuan-tieng-anh-lop-5-tap-2-205', '1625707845-BIABaitapcuoituanTAlop5Tap2.png', 1, 5, 'Nguyễn Song Hùng (Chủ biên) - An Thị Thu Hà - Trương Thị Ngọc Minh - Nguyễn Bích Thủy', NULL, '2018', '', 'https://hoclieu.sachmem.vn/question_set/5b432a364d16f43900ac6773/604370c7be2b37b770dd1b19/view/0', 204, 'https://hoclieu.sachmem.vn/question_set/5b432a364d16f43900ac6773/604370c7be2b37b770dd1b19/view/0', 'https://hoclieu.sachmem.vn/question_set/5b432a364d16f43900ac6773/604370c7be2b37b770dd1b19/view/0', '', '', '', '', '', '', '', '', '', 1, 2),
(206, 'Bài tập bổ trợ và nâng cao Tiếng Anh 7', 'bai-tap-bo-tro-va-nang-cao-tieng-anh-7-206', '1625708085-BiaBTbotrovanangcaotiengAnh7.png', 1, 7, 'Nguyễn Thị Chi (Chủ biên) - Nguyễn Hữu Cương', NULL, '2020', '<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">A. Cấu tr&uacute;c cuốn s&aacute;ch bao gồm:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">Phần 1: 12 đơn vị luyện tập (Tương ứng với 12 đơn vị b&agrave;i học trong s&aacute;ch gi&aacute;o khoa) v&agrave; 6 b&agrave;i kiểm tra giữa v&agrave; cuối mỗi k&igrave;. Mỗi đơn vị luyện tập c&oacute; 2 phần: Trọng t&acirc;m kiến thức cần nhớ v&agrave; Phần b&agrave;i tập;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">Phần 2: Đ&aacute;p &aacute;n</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">B. Nội dung cuốn s&aacute;ch:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">- Phần b&agrave;i tập bao gồm c&aacute;c b&agrave;i luyện đa dạng về ngữ &acirc;m, ngữ ph&aacute;p, từ vựng, đọc hiểu v&agrave; viết tương ứng theo nội dung c&aacute;c đơn vị b&agrave;i học của s&aacute;ch gi&aacute;o khoa Tiếng Anh 7.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">- Chủ đề v&agrave; nội dung phong ph&uacute;, hấp dẫn, ph&ugrave; hợp với lứa tuổi học sinh.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">- Đảm bảo t&iacute;nh tư tưởng v&agrave; t&iacute;nh khoa học.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">- Đảm bảo t&iacute;nh thực tiễn v&agrave; cập nhật.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">- C&oacute; tranh ảnh hấp dẫn học sinh.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">- Gi&uacute;p học sinh củng cố kiến thức đ&atilde; học, mở rộng, n&acirc;ng cao kiến thức v&agrave; kĩ năng ng&ocirc;n ngữ v&agrave; l&agrave;m quen với c&aacute;c dạng b&agrave;i thi v&agrave; kiểm tra.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">- Nội dung gắn với đời sống văn h&oacute;a x&atilde; hội của Việt Nam v&agrave; c&aacute;c nước, đặc biệt l&agrave; c&aacute;c nước n&oacute;i tiếng Anh.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'https://hoclieu.sachmem.vn/question_set/5b432ed63cb56637296ae807/60439221be2b37b770dd3a7d/view/0', 0, 'https://hoclieu.sachmem.vn/question_set/5b432ed63cb56637296ae807/60439221be2b37b770dd3a7d/view/0', 'https://hoclieu.sachmem.vn/question_set/5b432ed63cb56637296ae807/60439221be2b37b770dd3a7d/view/0', '', '', '', '', '', '', '', '', '', 1, 2),
