INSERT INTO `news` (`id`, `name`, `slug`, `img`, `date`, `description`, `content`, `source`, `iddm`) VALUES
(6, 'Nhà xuất bản Giáo dục Việt Nam tiếp tục mở miễn phí kho học liệu điện tử Sách Mềm', 'nha-xuat-ban-giao-duc-viet-nam-tiep-tuc-mo-mien-phi-kho-hoc-lieu-dien-tu-sach-mem-6', 'hoclieudientu.png', 1625535511, 'Kể từ khi dịch COVID-19 bùng phát, đây là lần thứ ba Công ty cổ phần Đầu tư và Phát triển Giáo dục Hà Nội mở miễn phí kho học liệu cho giáo viên và học sinh.', '<p>Ng&agrave;y 11/5/2021, C&ocirc;ng ty CP Đầu tư v&agrave; Ph&aacute;t triển Gi&aacute;o dục H&agrave; Nội, đơn vị th&agrave;nh vi&ecirc;n của Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam đ&atilde; ph&aacute;t th&ocirc;ng b&aacute;o mở miễn ph&iacute; kho học liệu điện tử S&aacute;ch Mềm tại địa chỉ sachmem.vn phục vụ gi&aacute;o vi&ecirc;n v&agrave; học sinh tr&ecirc;n to&agrave;n quốc.</p>\r\n\r\n<p>C&aacute;c thầy c&ocirc; v&agrave; c&aacute;c em học sinh chỉ cần đăng k&yacute; t&agrave;i khoản S&aacute;ch Mềm để được truy cập sử dụng gần 300 s&aacute;ch gi&aacute;o khoa, s&aacute;ch tham khảo, học liệu điện tử c&aacute;c m&ocirc;n học; hơn 300.000 c&acirc;u hỏi/ b&agrave;i tập.&nbsp;<img alt=\"\" src=\"https://liengtam123.tk/uploads/files/10.jpg\" style=\"height:899px; width:1400px\" /></p>\r\n\r\n<p>C&ocirc;ng cụ Quản l&yacute; học tập kết hợp với kho t&agrave;i nguy&ecirc;n học liệu điện tử sẵn c&oacute;, gi&aacute;o vi&ecirc;n c&oacute; thể tạo lớp học, quản lý lớp học, t&ugrave;y biến kh&oacute;a học chuẩn theo s&aacute;ch, tạo c&acirc;u hỏi, tạo b&agrave;i tập, giao b&agrave;i tập, tạo b&agrave;i kiểm tra, giao b&agrave;i kiểm tra, chia sẻ dữ liệu c&aacute; nh&acirc;n, chấm b&agrave;i trắc nghiệm tự động, ứng dụng tr&iacute; tuệ nh&acirc;n tạo hỗ trợ gi&aacute;o vi&ecirc;n chấm b&agrave;i trực tuyến, quản l&iacute; kết quả học tập, gửi th&ocirc;ng b&aacute;o, nhắn tin trao đổi (giữa gi&aacute;o vi&ecirc;n, học sinh, phụ huynh).</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của c&ocirc; Thu Hương, gi&aacute;o vi&ecirc;n m&ocirc;n Tiếng Anh tại Ba V&igrave;, H&agrave; Nội: &ldquo;Đối với gi&aacute;o vi&ecirc;n giảng dạy bộ m&ocirc;n Tiếng Anh như ch&uacute;ng t&ocirc;i, S&aacute;ch Mềm l&agrave; người bạn đồng h&agrave;nh kh&ocirc;ng thể thiếu. Hệ thống phần mềm tương t&aacute;c kết hợp với kho học liệu với h&agrave;ng trăm đầu s&aacute;ch của NXBGD thực sự hữu &iacute;ch. C&ocirc;ng nghệ gi&uacute;p ch&uacute;ng t&ocirc;i tiếp cận v&agrave; thao t&aacute;c dễ d&agrave;ng, đồng thời c&oacute; thời gian để nghi&ecirc;n cứu s&acirc;u về chuy&ecirc;n m&ocirc;n b&agrave;i dạy. Từ khi c&oacute; S&aacute;ch Mềm giờ học Tiếng Anh trở n&ecirc;n hấp dẫn, sinh động, c&aacute;c em học sinh h&agrave;o hứng v&agrave; t&iacute;ch cực nắm bắt b&agrave;i học&rdquo;.&nbsp;</p>\r\n\r\n<p><img alt=\"Giáo viên sử dụng Sách Mềm trong giảng dạy môn Tiếng Anh. \" src=\"https://media.vov.vn/sites/default/files/styles/large/public/2021-05/1_114.jpg\" /></p>\r\n\r\n<p>Được biết từ khi dịch COVID-19 b&ugrave;ng ph&aacute;t đ&acirc;y l&agrave; lần thứ ba C&ocirc;ng ty CP Đầu tư v&agrave; Ph&aacute;t triển Gi&aacute;o dục H&agrave; Nội mở miễn ph&iacute; kho học liệu cho gi&aacute;o vi&ecirc;n v&agrave; học sinh. Điều n&agrave;y thể hiện tr&aacute;ch nhiệm của doanh nghiệp với x&atilde; hội trong bối cảnh dịch bệnh diễn biến phức tạp đồng thời cũng thể hiện sự cam kết đồng h&agrave;nh với gi&aacute;o vi&ecirc;n, học sinh sử dụng bộ SGK Tiếng Anh của Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam./.</p>\r\n', '', 1),
(7, 'Sách giáo khoa tiếng Anh 6 theo chương trình giáo dục phổ thông 2018', 'sach-giao-khoa-tieng-anh-6-theo-chuong-trinh-giao-duc-pho-thong-2018-7', 'gtta6.png', 1625535525, 'Sách Tiếng Anh 6 của NXB Giáo dục Việt Nam theo Chương trình GDPT 2018 là sản phẩm kết hợp của các tác giả uy tín của Việt Nam kết hợp chặt chẽ với nhà xuất bản uy tín thế giới Pearson Education.', '<p>S&aacute;ch gi&aacute;o khoa Tiếng Anh 6 nằm trong bộ s&aacute;ch gi&aacute;o khoa theo Chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng 2018 do GS.TS. Ho&agrave;ng Văn V&acirc;n l&agrave; Tổng Chủ bi&ecirc;n l&agrave; sản phẩm của sự hợp t&aacute;c chặt chẽ về chuy&ecirc;n m&ocirc;n v&agrave; nghiệp vụ giữa Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam với Nh&agrave; xuất bản Pearson Education ở bậc học Trung học cơ sở v&agrave; Trung học phổ th&ocirc;ng . Nội dung s&aacute;ch b&aacute;m s&aacute;t&nbsp;<em>Chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng m&ocirc;n tiếng Anh</em>&nbsp;<em>v&agrave; Khung năng lực ngoại ngữ 6 bậc d&ugrave;ng cho Việt Nam</em>&nbsp;của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, đảm bảo t&iacute;nh li&ecirc;n th&ocirc;ng từ lớp 1 đến lớp 12.<img alt=\"\" src=\"https://liengtam123.tk/uploads/files/gtta6.png\" style=\"height:810px; width:1440px\" /></p>\r\n\r\n<p><strong>Dễ dạy, dễ học v&agrave; đ&aacute;p ứng s&aacute;t thực tế giảng dạy m&ocirc;n Tiếng Anh</strong></p>\r\n\r\n<p>S&aacute;ch gi&aacute;o khoa Tiếng Anh 6 mới c&oacute; nội dung kiến thức ph&ugrave; hợp với chương tr&igrave;nh GDPT tiếng Anh 2018 mới, cấu tr&uacute;c b&agrave;i học được bi&ecirc;n soạn dễ dạy, dễ tiếp cận, đ&aacute;p ứng s&aacute;t với thực tế dạy v&agrave; học m&ocirc;n Tiếng Anh tại trường học phổ th&ocirc;ng tại Việt Nam. Phần thu &acirc;m do đối t&aacute;c nước ngo&agrave;i của nh&agrave; xuất bản Pearson đảm nhận đảm bảo ph&aacute;t &acirc;m chuẩn. H&igrave;nh thức mỹ thuật của s&aacute;ch cũng tiệm cận với xu thể S&aacute;ch Tiếng Anh của c&aacute;c nh&agrave; xuất bản quốc tế.</p>\r\n\r\n<p>S&aacute;ch gi&aacute;o khoa Tiếng Anh 6 được bi&ecirc;n soạn theo đường hướng giao tiếp, lấy việc học l&agrave;m trung t&acirc;m nhằm ph&aacute;t huy to&agrave;n diện c&aacute;c kĩ năng ng&ocirc;n ngữ (nghe, n&oacute;i, đọc, viết). Năng lực giao tiếp bằng tiếng Anh của học sinh được ph&aacute;t triển th&ocirc;ng qua hoạt động học tập t&iacute;ch cực, chủ động, s&aacute;ng tạo.</p>\r\n\r\n<p>Hệ thống chủ điểm, chủ đề trong s&aacute;ch ph&ugrave; hợp với tư duy t&acirc;m l&yacute; lứa tuổi v&agrave; gần gũi với c&aacute;c hoạt động thường nhật của học sinh lớp 6. Th&ocirc;ng qua nội dung kiến thức S&aacute;ch gi&aacute;o khoa Tiếng Anh 6 g&oacute;p phần h&igrave;nh th&agrave;nh những phẩm chất v&agrave; năng lực cần thiết như &yacute; thức v&agrave; tr&aacute;ch nhiệm cộng đồng, khả năng th&iacute;ch ứng trong bối cảnh mới, h&igrave;nh th&agrave;nh th&oacute;i quen học tập suốt đời để trở th&agrave;nh những c&ocirc;ng d&acirc;n to&agrave;n cầu trong thời kỳ hội nhập.</p>\r\n\r\n<p>Trong s&aacute;ch Tiếng Anh 6, c&aacute;c gi&aacute; trị văn h&oacute;a Việt Nam được kết hợp h&agrave;i h&ograve;a với văn h&oacute;a khu vực, v&agrave; văn h&oacute;a quốc tế. Th&ocirc;ng qua s&aacute;ch gi&aacute;o khoa tiếng Anh 6, học sinh sẽ c&oacute; những hiểu biết kh&aacute;i qu&aacute;t về đất nước, con người, nền văn h&oacute;a c&aacute;c quốc gia n&oacute;i tiếng Anh v&agrave; của c&aacute;c quốc gia kh&aacute;c tr&ecirc;n thế giới, đồng thời c&oacute; hiểu biết, tự h&agrave;o về những gi&aacute; trị của nền văn h&oacute;a d&acirc;n tộc m&igrave;nh. Đồng thời, c&oacute; t&iacute;nh t&iacute;ch hợp tương quan với c&aacute;c m&ocirc;n học kh&aacute;c trong chương tr&igrave;nh đồng thời đảm bảo li&ecirc;n th&ocirc;ng từ lớp 1 đến lớp 12.</p>\r\n\r\n<p><img alt=\"\" src=\"https://liengtam123.tk/uploads/files/avt.png\" /></p>\r\n\r\n<p><strong>S&aacute;ch Mềm Hệ thống hợp phần bổ trợ phong ph&uacute;, đầy đủ v&agrave; lu&ocirc;n cập nhật</strong></p>\r\n\r\n<p>Điểm nổi bật của bộ s&aacute;ch gi&aacute;o khoa Tiếng Anh của Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam l&agrave; hệ thống hợp phần bổ trợ đi k&egrave;m v&ocirc; c&ugrave;ng đầy đủ, phong ph&uacute; đ&aacute;p ứng đầy đủ cho nhu cầu giảng dạy v&agrave; học tập bộ m&ocirc;n Tiếng Anh bao gồm S&aacute;ch Mềm, T&agrave;i liệu kiểm tra đ&aacute;nh gi&aacute;, B&agrave;i giảng điện tử PowerPoint, Video tiết giảng minh họa, Gi&aacute;o &aacute;n mẫu, Bộ truyện đọc, Audio CD, Ng&acirc;n h&agrave;ng kiểm tra đ&aacute;nh gi&aacute;&hellip;</p>\r\n\r\n<p>Đặc biệt, S&aacute;ch Mềm, phần mềm hỗ trợ giảng dạy v&agrave; kho học liệu được đ&aacute;nh gi&aacute; l&agrave; c&ocirc;ng cụ hỗ trợ &quot;kh&ocirc;ng thể thiếu&quot; của cộng đồng gi&aacute;o vi&ecirc;n Tiếng Anh to&agrave;n quốc từ bao nhi&ecirc;u năm nay tại trang web sachmem.vn.</p>\r\n\r\n<p>S&aacute;ch Mềm hiện đang c&oacute; hơn 1 triệu t&agrave;i khoản người d&ugrave;ng, cấp miễn ph&iacute; cho gi&aacute;o vi&ecirc;n Tiếng Anh tr&ecirc;n to&agrave;n quốc. Với hệ thống gần 300 đầu s&aacute;ch gi&aacute;o khoa, s&aacute;ch tham khảo, học liệu điện tử c&aacute;c m&ocirc;n học; 30.000 c&acirc;u hỏi kiểm tra đ&aacute;nh gi&aacute; thường xuy&ecirc;n v&agrave; định kỳ được chuẩn h&oacute;a theo định hướng ph&aacute;t triển năng lực, b&aacute;m s&aacute;t nội dung chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng mới.</p>\r\n\r\n<p><br />\r\nVới kho t&agrave;i nguy&ecirc;n học liệu điện tử sẵn c&oacute; như tr&ecirc;n, S&aacute;ch Mềm cho ph&eacute;p gi&aacute;o vi&ecirc;n c&oacute; thể tạo lớp học, quản l&yacute; lớp học, t&ugrave;y biến kh&oacute;a học chuẩn theo s&aacute;ch, tạo c&acirc;u hỏi, tạo b&agrave;i tập, giao b&agrave;i tập, tạo b&agrave;i kiểm tra, giao b&agrave;i kiểm tra, chia sẻ dữ liệu c&aacute; nh&acirc;n, chấm b&agrave;i trắc nghiệm tự động, ứng dụng tr&iacute; tuệ nh&acirc;n tạo hỗ trợ gi&aacute;o vi&ecirc;n chấm b&agrave;i trực tuyến, quản l&iacute; kết quả học tập, gửi th&ocirc;ng b&aacute;o, nhắn tin trao đổi (giữa gi&aacute;o vi&ecirc;n, học sinh, phụ huynh).</p>\r\n\r\n<p><strong>Hệ thống hỗ trợ, tập huấn đồng bộ tr&ecirc;n c&aacute;c nền tảng</strong></p>\r\n\r\n<p>Với kinh nghiệm bề d&agrave;y hơn 60 năm tổ chức bi&ecirc;n soạn s&aacute;ch gi&aacute;o khoa, Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam cam kết sẽ đồng h&agrave;nh c&ugrave;ng c&aacute;c thầy c&ocirc;, c&aacute;c em học sinh v&agrave; phụ huynh trong qu&aacute; tr&igrave;nh triển khai v&agrave; sử dụng s&aacute;ch gi&aacute;o khoa. Hệ thống hỗ trợ triển khai sử dụng s&aacute;ch gi&aacute;o khoa Tiếng Anh 6 bao gồm tập huấn gi&aacute;o vi&ecirc;n, c&aacute;c k&ecirc;nh tư vấn hỗ trợ được triển khai đầy đủ v&agrave; đồng bộ tr&ecirc;n tất cả c&aacute;c nền tảng.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', 1),
(8, 'Sách giáo khoa tiếng Anh 2 theo chương trình giáo dục phổ thông 2018', 'sach-giao-khoa-tieng-anh-2-theo-chuong-trinh-giao-duc-pho-thong-2018-8', 'TA2.png', 1625535540, 'Sách Tiếng Anh 2 của NXB Giáo dục Việt Nam theo Chương trình GDPT 2018 là sản phẩm kết hợp của các tác giả uy tín của Việt Nam kết hợp chặt chẽ với nhà xuất bản uy tín thế giới Macmillan Education.', '<p>S&aacute;ch Tiếng Anh 2 của Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam nằm trong Bộ s&aacute;ch gi&aacute;o khoa từ 1-12 theo Chương tr&igrave;nh Gi&aacute;o dục phổ th&ocirc;ng 2018 do GS.TS. Ho&agrave;ng Văn V&acirc;n l&agrave;m Tổng chủ bi&ecirc;n l&agrave; sản phẩm kết hợp của c&aacute;c t&aacute;c giả uy t&iacute;n h&agrave;ng đầu của Việt Nam kết hợp chuy&ecirc;n m&ocirc;n, nghiệp vụ chặt chẽ với nh&agrave; xuất bản uy t&iacute;n thế giới về S&aacute;ch Tiếng Anh ở bậc tiểu học, Macmillan Education.</p>\r\n\r\n<p>Nội dung s&aacute;ch b&aacute;m s&aacute;t với y&ecirc;u cầu của Chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng L&agrave;m quen tiếng Anh lớp 1 v&agrave; lớp 2 do Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Việt Nam ban h&agrave;nh.</p>\r\n\r\n<p><strong>Nội dung thiết kế đảm bảo yếu tố &quot;l&agrave;m quen&quot; theo chương tr&igrave;nh</strong></p>\r\n\r\n<p>Bộ s&aacute;ch được thiết kế ph&ugrave; hợp với điều kiện giảng dạy bộ m&ocirc;n Tiếng Anh trong nh&agrave; trường phổ th&ocirc;ng. H&igrave;nh thức mỹ thuật đẹp, hiện đại, ph&ugrave; hợp với xu thế thiết kế s&aacute;ch của c&aacute;c bộ s&aacute;ch tiếng Anh tr&ecirc;n thế giới. Đặc biệt, bản thu &acirc;m do đối t&aacute;c Macmillan Education thực hiện giọng Anh - Anh chuẩn mực, đạt chuẩn quốc tế.</p>\r\n\r\n<p><img alt=\"\" src=\"https://liengtam123.tk/uploads/files/TA2.png\" style=\"height:810px; width:1440px\" />S&aacute;ch Tiếng Anh 2 được bi&ecirc;n soạn theo đường hướng giao tiếp, lấy việc học l&agrave;m trung t&acirc;m nhằm ph&aacute;t huy to&agrave;n diện c&aacute;c kĩ năng ng&ocirc;n ngữ (nghe, n&oacute;i, đọc, viết). C&aacute;c hoạt động được thiết kế hấp dẫn, gần gũi, th&acirc;n thiện gi&uacute;p trẻ kh&aacute;m ph&aacute;, trải nghiệm, học qua chơi, đảm bảo yếu tố &quot;l&agrave;m quen&quot; h&igrave;nh th&agrave;nh niềm y&ecirc;u th&iacute;ch học tiếng Anh trong những năm đầu cấp.</p>\r\n\r\n<p>C&aacute;c chủ đề nội dung được x&acirc;y dựng ph&ugrave; hợp với tư duy v&agrave; t&acirc;m l&iacute; lứa tuổi của học sinh tiểu học đồng thời kết hợp h&agrave;i h&ograve;a c&aacute;c gi&aacute; trị văn h&oacute;a Việt Nam, văn h&oacute;a khu vực, quốc tế v&agrave; c&oacute; t&iacute;nh li&ecirc;n th&ocirc;ng t&iacute;ch hợp tương quan với c&aacute;c m&ocirc;n học kh&aacute;c trong chương tr&igrave;nh đồng thời đảm bảo li&ecirc;n th&ocirc;ng từ lớp 1 đến lớp 12.</p>\r\n\r\n<p><strong>Hệ thống hợp phần bổ trợ học liệu điện tử phong ph&uacute;, cập nhật</strong></p>\r\n\r\n<p>Điểm nổi bật của bộ s&aacute;ch gi&aacute;o khoa Tiếng Anh của Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam l&agrave; hệ thống hợp phần bổ trợ đi k&egrave;m v&ocirc; c&ugrave;ng đầy đủ, phong ph&uacute; đ&aacute;p ứng đầy đủ cho nhu cầu giảng dạy v&agrave; học tập bộ m&ocirc;n Tiếng Anh bao gồm T&agrave;i liệu kiểm tra đ&aacute;nh gi&aacute;, B&agrave;i giảng điện tử PowerPoint, Video tiết giảng minh họa, Gi&aacute;o &aacute;n mẫu, Vở tập viết, Bộ truyện đọc, Bộ Thẻ từ, Audio CD&hellip;</p>\r\n\r\n<p>Đặc biệt, S&aacute;ch Mềm, phần mềm hỗ trợ giảng dạy v&agrave; kho học liệu được đ&aacute;nh gi&aacute; l&agrave; c&ocirc;ng cụ hỗ trợ &quot;kh&ocirc;ng thể thiếu&quot; của cộng đồng gi&aacute;o vi&ecirc;n Tiếng Anh to&agrave;n quốc từ bao nhi&ecirc;u năm nay tại trang web sachmem.vn.</p>\r\n\r\n<p>S&aacute;ch Mềm hiện đang c&oacute; hơn 1 triệu t&agrave;i khoản người d&ugrave;ng, cấp miễn ph&iacute; cho gi&aacute;o vi&ecirc;n Tiếng Anh tr&ecirc;n to&agrave;n quốc. Với hệ thống gần 300 đầu s&aacute;ch gi&aacute;o khoa, s&aacute;ch tham khảo, học liệu điện tử c&aacute;c m&ocirc;n học; 30.000 c&acirc;u hỏi kiểm tra đ&aacute;nh gi&aacute; thường xuy&ecirc;n v&agrave; định kỳ được chuẩn h&oacute;a theo định hướng ph&aacute;t triển năng lực, b&aacute;m s&aacute;t nội dung chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng mới.</p>\r\n\r\n<p>Với kho t&agrave;i nguy&ecirc;n học liệu điện tử sẵn c&oacute; như tr&ecirc;n, S&aacute;ch Mềm cho ph&eacute;p gi&aacute;o vi&ecirc;n c&oacute; thể tạo lớp học, quản l&yacute; lớp học, t&ugrave;y biến kh&oacute;a học chuẩn theo s&aacute;ch, tạo c&acirc;u hỏi, tạo b&agrave;i tập, giao b&agrave;i tập, tạo b&agrave;i kiểm tra, giao b&agrave;i kiểm tra, chia sẻ dữ liệu c&aacute; nh&acirc;n, chấm b&agrave;i trắc nghiệm tự động, ứng dụng tr&iacute; tuệ nh&acirc;n tạo hỗ trợ gi&aacute;o vi&ecirc;n chấm b&agrave;i trực tuyến, quản l&iacute; kết quả học tập, gửi th&ocirc;ng b&aacute;o, nhắn tin trao đổi (giữa gi&aacute;o vi&ecirc;n, học sinh, phụ huynh).</p>\r\n\r\n<p><strong>Hệ thống hỗ trợ triển khai sử dụng s&aacute;ch đầy đủ v&agrave; đồng bộ</strong></p>\r\n\r\n<p>Được biết, trong một cuộc khảo s&aacute;t gần đ&acirc;y tr&ecirc;n Cộng đồng gi&aacute;o vi&ecirc;n S&aacute;ch Mềm, S&aacute;ch Gi&aacute;o Khoa Tiếng Anh của Tổng chủ bi&ecirc;n GS.TS Ho&agrave;ng Văn V&acirc;n được đ&aacute;nh gi&aacute; rất cao (82 % gi&aacute;o vi&ecirc;n tham gia khảo s&aacute;t đ&aacute;nh gi&aacute; ở khung điểm từ 8-10/10 v&agrave; đều h&agrave;i l&ograve;ng với chất lượng của bộ s&aacute;ch).</p>\r\n\r\n<p><img alt=\"\" src=\"https://liengtam123.tk/uploads/files/H%E1%BB%A3p-ph%E1%BA%A7n-b%E1%BB%95-tr%E1%BB%A3.png\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<p>Với kinh nghiệm bề d&agrave;y hơn 60 năm tổ chức bi&ecirc;n soạn s&aacute;ch gi&aacute;o khoa, Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam cam kết sẽ đồng h&agrave;nh c&ugrave;ng c&aacute;c thầy c&ocirc;, c&aacute;c em học sinh v&agrave; phụ huynh trong qu&aacute; tr&igrave;nh triển khai v&agrave; sử dụng s&aacute;ch gi&aacute;o khoa. Hệ thống hỗ trợ triển khai sử dụng s&aacute;ch gi&aacute;o khoa Tiếng Anh 2 bao gồm tập huấn gi&aacute;o vi&ecirc;n, c&aacute;c k&ecirc;nh tư vấn hỗ trợ được triển khai đầy đủ v&agrave; đồng bộ tr&ecirc;n tất cả c&aacute;c nền tảng.</p>\r\n', '', 1),
(9, 'Tập huấn giáo viên sử dụng sách giáo khoa Tiếng Anh 2 và Tiếng Anh 6', 'tap-huan-giao-vien-su-dung-sach-giao-khoa-tieng-anh-2-va-tieng-anh-6-9', 'thiết-kế-cv-011.png', 1624612372, 'Ngày 26/5/2021, Nhà Xuất bản Giáo dục Việt Nam tổ chức  2 buổi tập huấn sử dụng SGK Tiếng Anh 2 và Tiếng Anh 6 cho giáo viên bộ môn Tiếng Anh.', '<p>Ng&agrave;y 26/5/2021, Nh&agrave; Xuất bản Gi&aacute;o dục Việt Nam tổ chức 2 buổi tập huấn sử dụng SGK Tiếng Anh 2 v&agrave; Tiếng Anh 6 cho gi&aacute;o vi&ecirc;n bộ m&ocirc;n Tiếng Anh địa b&agrave;n tỉnh Lai Ch&acirc;u với h&igrave;nh thức trực tuyến. Tham dự buổi tập huấn l&agrave; 52 điểm cầu đối với lớp 2 v&agrave; 139 điểm cầu đối với lớp 6. Đ&acirc;y l&agrave; sự kiện mở đầu cho chuỗi sự kiện tập huấn cho gi&aacute;o vi&ecirc;n giảng dạy bộ m&ocirc;n Tiếng Anh sử dụng SGK của NXBGDVN.<img alt=\"\" src=\"https://liengtam123.tk/uploads/files/minh%20h%E1%BB%8Da.jpg\" /></p>\r\n\r\n<p>Trước diễn biến phức tạp của dịch COVID-19, NXBGDVN đ&atilde; chủ động x&acirc;y dựng chương tr&igrave;nh tập huấn sử dụng SGK Tiếng Anh lớp 2, lớp 6 mới theo c&aacute;c h&igrave;nh thức trực tuyến, trực tiếp v&agrave; tự tập huấn.</p>\r\n\r\n<p>Đội ngũ b&aacute;o c&aacute;o vi&ecirc;n chất lượng l&agrave; c&aacute;c t&aacute;c giả, c&aacute;c giảng vi&ecirc;n đến từ khoa Sư phạm Tiếng Anh c&aacute;c Trường Đại học lớn, c&aacute;c chuy&ecirc;n gia l&agrave; chuy&ecirc;n vi&ecirc;n Tiếng Anh của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo.</p>\r\n\r\n<p>Nội dung b&aacute;o c&aacute;o được thiết kế tập trung v&agrave;o triển khai phương ph&aacute;p giảng dạy với s&aacute;ch gi&aacute;o khoa mới, ph&ugrave; hợp với t&igrave;nh h&igrave;nh của địa phương.</p>\r\n\r\n<p>Ngo&agrave;i ra bộ t&agrave;i liệu tập huấn, c&aacute;c hợp phần bổ trợ đ&atilde; được cập nhật đầy đủ trong t&agrave;i khoản gi&aacute;o vi&ecirc;n S&aacute;ch Mềm để c&aacute;c thầy c&ocirc; c&oacute; thể nghi&ecirc;n cứu, tham khảo, tự tập huấn.</p>\r\n\r\n<p>C&ocirc;ng t&aacute;c tập huấn sẽ được NXBGDVN được triển khai từ th&aacute;ng 5/2021, đảm bảo ho&agrave;n th&agrave;nh trước 31/7/2021 theo chỉ đạo của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo.</p>\r\n', '', 1),
(15, 'Lộ trình học Tiếng Anh luyện thi Đại học thế nào là tốt nhất?', 'lo-trinh-hoc-tieng-anh-luyen-thi-dai-hoc-the-nao-la-tot-nhat-15', 'lộ-trình.png', 1625535676, 'Tự ôn tiếng Anh cho kì thi THPT Quốc Gia đã không còn là câu chuyện xa lạ đối với các bạn học sinh ngày nay. Vậy bạn cần ôn tiếng anh như thế nào để có thể dễ dàng đạt được điểm mong muốn trong kì thi đại học sắp tới?', '<p>LỘ TR&Igrave;NH HỌC TIẾNG ANH LUYỆN THI ĐẠI HỌC THẾ N&Agrave;O L&Agrave; TỐT NHẤT?<br />\r\nNhiều bạn inbox/comment hỏi về việc x&acirc;y dựng lộ tr&igrave;nh học Tiếng Anh thế n&agrave;o khi m&agrave; k&igrave; thi đại học đ&atilde; sắp diễn ra.<br />\r\n- C&ocirc; ơi em c&oacute; n&ecirc;n luyện đề lu&ocirc;n kh&ocirc;ng ạ?<br />\r\n- C&ocirc; ơi em mất gốc to&agrave;n bộ th&igrave; n&ecirc;n học g&igrave; trước ạ<br />\r\n- C&ocirc; ơi em đang ở mức kh&aacute;, thi thử được 7 điểm th&igrave; n&ecirc;n học thế n&agrave;o để l&ecirc;n mức 8+ ạ?<br />\r\n<br />\r\nỞ b&agrave;i chia sẻ n&agrave;y S&aacute;ch Mềm sẽ hướng dẫn c&aacute;c bạn tự x&acirc;y dựng 1 lộ tr&igrave;nh ph&ugrave; hợp nhất nh&eacute;.<br />\r\n<br />\r\n***C&aacute;c em chia sẻ b&agrave;i n&agrave;y về để tham khảo lại trong qu&aacute; tr&igrave;nh học sau n&agrave;y nữa nh&eacute;<br />\r\n<br />\r\nĐỐI VỚI C&Aacute;C BẠN MẤT GỐC (thi thử &lt;6 điểm)<br />\r\n<br />\r\n- HIện tại c&aacute;c bạn ở mức n&agrave;y đang rất hổng kiến thức, cần phải &ocirc;n tập lại to&agrave;n bộ cả từ vựng v&agrave; ngữ ph&aacute;p trước rồi mới c&oacute; thể bắt đầu tham gia luyện đề<br />\r\n<br />\r\n- C&aacute;c bạn c&oacute; thể x&acirc;y dựng lộ tr&igrave;nh học như sau:<br />\r\n+ Đầu ti&ecirc;n, c&aacute;c bạn tập trung lấy lại gốc v&agrave; l&agrave;m quen với c&aacute;c dạng b&agrave;i tập trong đề thi đại học<br />\r\n+ Chia thời gian học song song trong 1 tuần, tối thiểu 4 buổi 1 tuần tự học. 2 ng&agrave;y c&aacute;c bạn học ngữ ph&aacute;p kết hợp từ vựng, 2 ng&agrave;y c&aacute;c bạn tập giải c&aacute;c dạng b&agrave;i trong đề trong đề đại học.<br />\r\nLưu &yacute;: c&aacute;c c&acirc;u kh&oacute; v&agrave; qu&aacute; sức c&aacute;c bạn n&ecirc;n bỏ qua, tập trung l&agrave;m c&aacute;c c&acirc;u vừa sức m&igrave;nh để lấy lại mức điểm 7 trước<br />\r\nV&iacute; dụ: Trọng &acirc;m ph&aacute;t &acirc;m - Đọc Hiểu - T&igrave;m lỗi sai - Chọn đ&aacute;p &aacute;n đ&uacute;ng.<br />\r\n+ Luyện từ vựng hằng ng&agrave;y bằng c&aacute;ch tập đặt c&acirc;u, giải b&agrave;i tập nằm trong s&aacute;ch luyện v&agrave; tr&ecirc;n Sachmem.vn<br />\r\n<br />\r\nĐỐI VỚI C&Aacute;C MỨC KH&Aacute; (thi thử &gt;6 điểm)<br />\r\n<br />\r\nỞ mức n&agrave;y c&aacute;c bạn đ&atilde; c&oacute; 1 vốn từ vựng v&agrave; ngữ ph&aacute;p tương đối, nhưng c&ograve;n cần luyện tập bổ sung kỹ năng l&agrave;m b&agrave;i v&agrave; giải th&ecirc;m c&aacute;c c&acirc;u n&acirc;ng cao để đạt điểm 8+.<br />\r\n<br />\r\nLộ tr&igrave;nh ph&ugrave; hợp với c&aacute;c bạn như sau:<br />\r\n+ C&aacute;c bạn tập trung &ocirc;n lại c&aacute;c dạng ngữ ph&aacute;p c&oacute; trong đề thi đại học v&agrave; kết hợp học th&ecirc;m c&aacute;c dạng ngữ ph&aacute;p mức n&acirc;ng cao hơn để hướng tới mục ti&ecirc;u 8+<br />\r\n+ D&agrave;nh 1 th&aacute;ng cuối cho việc luyện theo chủ đề từng dạng b&agrave;i trong đề thi đại học.<br />\r\nV&iacute; dụ: Tuần 1: Trọng &acirc;m ph&aacute;t &acirc;m, tuần 2: Ngữ ph&aacute;p c&acirc;u đ&uacute;ng c&acirc;u sai, tuần 3: T&igrave;m lỗi sai, Tuần 4: đọc hiểu<br />\r\nLuyện thật thuần thục c&aacute;c dạng n&agrave;y, l&agrave;m tối thiểu 500 c&acirc;u thật nhuần nhuyễn th&igrave; m&igrave;nh mới chuyển sang dạng kh&aacute;c, tập giải th&ecirc;m c&aacute;c c&acirc;u n&acirc;ng cao mức 8+<br />\r\n+ Luyện th&ecirc;m từ vựng, đặc biệt l&agrave; trong b&agrave;i đọc hiểu. Từ vựng thi đại học sẽ nằm d&agrave;n trải ở 3 năm học nhưng tập trung nhiều nhất l&agrave; lớp 12. C&aacute;c em &ocirc;n từ vựng lại theo chủ đề để kh&ocirc;ng bị gặp kh&oacute; khi gặp từ mới trong b&agrave;i.<br />\r\n<br />\r\nChung cho cả 2 nh&oacute;m: th&aacute;ng cuối m&igrave;nh dồn sức tập trung v&agrave;o giải đề tổng hợp. L&agrave;m thật nhiều đề nhất c&oacute; thể, note ghi ch&uacute; ngữ ph&aacute;p từ vựng m&igrave;nh c&ograve;n yếu trong qu&aacute; tr&igrave;nh giải đề v&agrave; mở lại phần kiến thức n&agrave;y để &ocirc;n lại thật chắc.</p>\r\n', '', 1),
(18, 'Khuyến cáo giáo viên, phụ huynh, học sinh không mua sách trôi nổi', 'khuyen-cao-giao-vien-phu-huynh-hoc-sinh-khong-mua-sach-troi-noi-18', 'huong-dan-mua-sgk-3.jpg', 1625623757, 'Gần 3 triệu cuốn sách giả, trong đó đa số là sách giả sản phẩm của NXBGD chuẩn bị đưa đi tiêu thụ tại các tỉnh, thành phố vừa được phát hiện tại Hà Nội.', '<p>Mới đ&acirc;y Cục Cảnh s&aacute;t điều tra tội phạm về tham nhũng, kinh tế, bu&ocirc;n lậu -Bộ C&ocirc;ng an đ&atilde; phối hợp với Bộ tư lệnh Cảnh s&aacute;t cơ động, C&ocirc;ng an H&agrave; Nội v&agrave; NXB GDVN ph&aacute;t hiện một đường d&acirc;y in, gia c&ocirc;ng, ti&ecirc;u thụ gần 3 triệu cuốn s&aacute;ch giả.</p>\r\n\r\n<p>Đ&aacute;ng n&oacute;i, trong số s&aacute;ch giả n&agrave;y đa số s&aacute;ch l&agrave;m giả sản phẩm của Nh&agrave; xuất bản Gi&aacute;o dục. Lợi nhuận thu được từ số s&aacute;ch giả n&agrave;y khoảng 50 tỉ đồng.&nbsp;</p>\r\n\r\n<p>Trước t&igrave;nh trạng s&aacute;ch gi&aacute;o dục bị in lậu tr&agrave;n lan với số lượng lớn, thủ đoạn tinh vi, Bộ GD&amp;ĐT đ&atilde; c&oacute; văn bản gửi Bộ C&ocirc;ng thương đề nghị phối hợp xử l&yacute; t&igrave;nh trạng s&aacute;ch gi&aacute;o dục bị l&agrave;m giả x&acirc;m nhập v&agrave;o c&aacute;c nh&agrave; trường&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"https://liengtam123.tk/uploads/files/huong-dan-mua-sgk-3.jpg\" style=\"height:1200px; width:1600px\" /></p>\r\n\r\n<p>Về ph&iacute;a Nh&agrave; xuất bản Gi&aacute;o dục, đ&atilde; phối hợp với c&aacute;c cơ quan chức năng trong việc cung cấp v&agrave; x&aacute;c minh th&ocirc;ng tin, cung cấp s&aacute;ch mẫu v&agrave; hồ sơ ph&aacute;p l&yacute; li&ecirc;n quan đến c&aacute;c vụ việc tổ chức, c&aacute; nh&acirc;n sản xuất, kinh doanh xuất bản phẩm lậu để xử l&yacute; theo quy định của ph&aacute;p luật.</p>\r\n\r\n<p>Mặt kh&aacute;c, theo NXBGD, để đấu tranh chống xuất bản phẩm lậu một c&aacute;ch hiệu quả, cần sự phối hợp của nhiều cơ quan, tổ chức v&agrave; của to&agrave;n x&atilde; hội.</p>\r\n\r\n<p>Hơn thế, c&aacute;c NXB, c&aacute;c tổ chức li&ecirc;n quan v&agrave; bạn đọc&hellip; cần coi đ&acirc;y l&agrave; nhiệm vụ quan trọng, nhận thức đầy đủ tr&aacute;ch nhiệm của m&igrave;nh đối với c&ocirc;ng t&aacute;c n&agrave;y, ki&ecirc;n tr&igrave;, ki&ecirc;n quyết đẩy l&ugrave;i xuất bản phẩm lậu.</p>\r\n\r\n<p>PGS.TS Nguyễn Văn T&ugrave;ng - Ph&oacute; tổng bi&ecirc;n tập NXBGD cũng đưa ra khuyến c&aacute;o GV, phụ huynh v&agrave; HS n&ecirc;n t&igrave;m mua s&aacute;ch tại c&aacute;c hệ thống ph&acirc;n phối ch&iacute;nh thức của NXB, c&aacute;c c&ocirc;ng ty S&aacute;ch - Thiết bị trường học địa phương. Kh&ocirc;ng mua s&aacute;ch từ c&aacute;c nguồn tr&ocirc;i nổi tr&ecirc;n thị trường để tr&aacute;nh mua phải s&aacute;ch giả.</p>\r\n\r\n<p>Những cuốn SGK, s&aacute;ch tham khảo, s&aacute;ch b&agrave;i tập giả hoặc bị in lậu c&oacute; thể bị sai lệch th&ocirc;ng tin, nội dung, chất lượng; kh&ocirc;ng thể k&iacute;ch hoạt m&atilde; để sử dụng nguồn t&agrave;i nguy&ecirc;n hỗ trợ của NXBGD, c&oacute; thể ảnh hưởng nguy hại đến việc tiếp thu kiến thức của học sinh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Nguồn: B&aacute;o Gi&aacute;o dục v&agrave; Thời đại</em></p>\r\n', '', 4),
(19, 'Hội nghị học tập, quán triệt và triển khai thực hiện nghị quyết hội nghị lần thứ 6 BCH Trung ương Đảng (Khóa XII)', 'hoi-nghi-hoc-tap-quan-triet-va-trien-khai-thuc-hien-nghi-quyet-hoi-nghi-lan-thu-6-bch-trung-uong-dang-khoa-xii-19', '2(12).jpg', 1625624232, 'Ngày 23/05/2018, Đảng ủy NXBGDVN tổ chức Hội nghị học tập, quán triệt và triển khai thực hiện Nghị quyết Hội nghị lần thứ 6 BCH Trung ương Đảng (Khóa XII).', '<p><strong>Ng&agrave;y 23/05/2018, Đảng ủy NXBGDVN tổ chức&nbsp;</strong><strong>Hội nghị học tập, qu&aacute;n triệt v&agrave; triển khai thực hiện Nghị quyết Hội nghị lần thứ 6 BCH Trung ương Đảng (Kh&oacute;a XII).</strong></p>\r\n\r\n<p>Đồng ch&iacute; Nguyễn Đức Th&aacute;i - Ủy vi&ecirc;n thường vụ Đảng ủy Bộ GD&amp;ĐT, B&iacute; thư Đảng ủy NXBGDVN, Chủ tịch HĐTV NXBGDVN - tới dự v&agrave; trực tiếp truyền đạt, qu&aacute;n triệt, triển khai thực hiện nội dung Nghị quyết Hội nghị Trung ương 6, kh&oacute;a XII.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://liengtam123.tk/uploads/files/1(19).jpg\" style=\"height:954px; width:1429px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:10px\"><em>Đồng ch&iacute; Ho&agrave;ng L&ecirc; B&aacute;ch &ndash; Ph&oacute; B&iacute; thư Đảng ủy, Tổng Gi&aacute;m đốc NXBGDVN</em></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:10px\"><em>&nbsp;ph&aacute;t biểu khai mạc Hội nghị</em></span></p>\r\n\r\n<p>Tại Hội nghị, đồng ch&iacute; Nguyễn Đức Th&aacute;i truyền đạt những nội dung cơ bản của Nghị quyết Hội nghị Trung ương 6, kh&oacute;a XII bao gồm 4 Nghị quyết: Nghị quyết số 18-NQ/TW &ldquo;Một số vấn đề về tiếp tục đổi mới, sắp xếp tổ chức bộ m&aacute;y của hệ thống ch&iacute;nh trị tinh gọn, hoạt động hiệu lực, hiệu quả&rdquo;; Nghị quyết số 19-NQ/TW &ldquo;Về tiếp tục đổi mới hệ thống tổ chức v&agrave; quản l&yacute;, n&acirc;ng cao chất lượng v&agrave; hiệu quả hoạt động của c&aacute;c đơn vị sự nghiệp c&ocirc;ng lập&rdquo;; Nghị quyết số 20-NQ/TW &ldquo;Về tăng cường c&ocirc;ng t&aacute;c bảo vệ, chăm s&oacute;c v&agrave; n&acirc;ng cao sức khỏe nh&acirc;n d&acirc;n trong t&igrave;nh h&igrave;nh mới&rdquo;; Nghị quyết số 21-NQ/TW &ldquo;Về c&ocirc;ng t&aacute;c d&acirc;n số trong t&igrave;nh h&igrave;nh mới&rdquo;.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://liengtam123.tk/uploads/files/2(12).jpg\" style=\"height:954px; width:1429px\" /><span style=\"font-size:10px\"><em>Đồng ch&iacute; Nguyễn Đức Th&aacute;i - Ủy vi&ecirc;n thường vụ Đảng ủy Bộ GD&amp;ĐT, B&iacute; thư Đảng ủy NXBGDVN, </em></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:10px\"><em>Chủ tịch HĐTV NXBGDVN<em>&nbsp;ph&aacute;t biểu tại hội nghị</em></em></span></p>\r\n\r\n<p>Đồng ch&iacute; Nguyễn Đức Th&aacute;i nhấn mạnh một số vấn đề quan trọng trong 4 nghị quyết tr&ecirc;n. Đ&oacute; l&agrave; những quyết s&aacute;ch quan trọng của Đảng, đề cập đến nhiều vấn đề rộng lớn, hệ trọng của đất nước, vừa cơ bản, vừa cấp b&aacute;ch v&agrave; l&agrave; sự cụ thể h&oacute;a c&aacute;c nhiệm vụ trọng t&acirc;m m&agrave; Nghị quyết Đại hội XII của Đảng đề ra.</p>\r\n\r\n<p>Đồng ch&iacute; Nguyễn Đức Th&aacute;i cũng d&agrave;nh thời gian ph&acirc;n t&iacute;ch đặc th&ugrave; của NXBGDVN trong giai đoạn bi&ecirc;n soạn SGK mới; giao nhiệm vụ chủ tr&igrave;, đầu mối cho từng đơn vị, y&ecirc;u cầu x&acirc;y dựng chỉ ti&ecirc;u cụ thể về sắp xếp lại bộ m&aacute;y, vị tr&iacute; việc l&agrave;m, đẩy mạnh cải c&aacute;ch h&agrave;nh ch&iacute;nh, tăng cường bảo vệ chăm s&oacute;c sức khỏe cho người lao động...</p>\r\n\r\n<p><em>Cũng tại hội nghị, đồng ch&iacute; Ho&agrave;ng L&ecirc; B&aacute;ch - Ph&oacute; B&iacute; thư đảng ủy, Chủ nhiệm Ủy ban Kiểm tra Đảng ủy NXBGDVN, Tổng Gi&aacute;m đốc NXBGDVN - đ&atilde; th&ocirc;ng tin về Quy định số 102-QĐ/TW ng&agrave;y 15/11/2017 về xử l&iacute; kỷ luật đảng vi&ecirc;n vi phạm của Ban Chấp h&agrave;nh Trung ương v&agrave; hướng dẫn số 04-HD/UBKTTW của Ủy ban Kiểm tra Trung ương, ng&agrave;y 22/3/2018 về thực hiện một số điều trong Quy định số 102.</em></p>\r\n\r\n<p>C&aacute;c nội dung tr&ecirc;n sẽ được triển khai cụ thể tới từng đảng vi&ecirc;n v&agrave; thực hiện tại c&aacute;c đơn vị thuộc NXBGDVN.&nbsp;</p>\r\n\r\n<p><em>Nguồn: Nh&agrave; xuất bản Gi&aacute;o dục Việt Nam</em></p>\r\n', '', 2);
